mapper.select:
SELECT id
FROM petusers
WHERE id=#{id}

SELECT id,pwd,name,email,phoneNumber,address,household,profile,TO_CHAR(regdate,'YYYY-MM-DD')AS regdate,groupNum
FROM petusers
WHERE id=#{id}

mapper.update:
UPDATE petusers
SET pwd=#{pwd}
WHERE id=#{id}

UPDATE petusers
SET profile=#{profile},email=#{email},phoneNumber=#{phoneNumber},address=#{address},household=#{household, jdbcType=VARCHAR}
WHERE id=#{id}
